{
  title: 'Dragone/UI/Button',
  component: Button,
  tags: ['autodocs'],
  args: {
    focusVisible: fn(),
    label: 'Dragone',
    disabled: false,
    icon: false,
    showLeadingIcon: false,
    showTrailingIcon: false,
    size: 'large',
    variant: 'primary',
  },
  argTypes: {
    focusVisible: { type: 'function', control: false },
    label: { control: 'text' },
    showLeadingIcon: { control: 'boolean' },
    showTrailingIcon: { control: 'boolean' },
    size: {
      description: 'The size of the button',
      type: 'string',
      options: ['small', 'medium', 'large'],
      control: { type: 'select' },
      table: {
        defaultValue: { summary: 'large' },
      },
    },
    variant: {
      description: 'The variant of the button',
      type: 'string',
      options: ['primary', 'secondary', 'tertiary', 'ghost', 'danger'],
      control: { type: 'select' },
      table: {
        defaultValue: { summary: 'primary' },
      },
    },
    disabled: {
      description: 'Whether the button is disabled',
      type: 'boolean',
      control: { type: 'boolean' },
      table: {
        defaultValue: { summary: 'false' },
      },
    },
  },
  decorators: [
    moduleMetadata({ providers: [provideIcons({ faSolidArrowRight })], imports: [NgIcon] }),
  ],
  render: args => {
    return {
      props: args,
      template: `
      <button drgn-button ${argsToTemplate(args)}>
        ${args.showLeadingIcon ? '<ng-icon slot="leading" name="fa-solid-arrow-right"></ng-icon>' : ''}
        ${args.label}
        ${args.showTrailingIcon ? '<ng-icon slot="trailing" name="fa-solid-arrow-right"></ng-icon>' : ''}
      </button>
      `,
    };
  },
}

{
  parameters: {
    controls: {
      matchers: {
        color: /(background|color)$/i,
        date: /Date$/i,
      },
    },
  },
  decorators: [
    componentWrapperDecorator(
      story =>
        `<div style="display: flex; justify-content: center; align-items: center;">${story}</div>`,
    ),
  ],
}

{
  play: async ({ canvas, userEvent, canvasElement, args, step }) => {
    const button = canvas.getByRole<HTMLButtonElement>('button');
    await step('Click on button', async () => {
      await userEvent.click(button);
      await expect(args.focusVisible).not.toHaveBeenCalled();
    });

    await userEvent.click(canvasElement);

    await step('Focus visible on button', async () => {
      await userEvent.tab();
      await expect(button).toHaveFocus();
      await expect(args.focusVisible).toHaveBeenCalled();
    });
  },
}

{
  ...Template,
  args: {
    variant: 'tertiary',
  },
  render: args => ({
    props: args,
    template: `
      <div class="drgn-dark" style="background-color: #002460">
        <button drgn-button ${argsToTemplate(args)}>
          ${args.showLeadingIcon ? '<ng-icon slot="leading" name="fa-solid-arrow-right"></ng-icon>' : ''}
          ${args.label}
          ${args.showTrailingIcon ? '<ng-icon slot="trailing" name="fa-solid-arrow-right"></ng-icon>' : ''}
        </button>
      </div>
    `,
  }),
}

